settings.markdownTextFunction:
let html = '<div style="width: 100%; height: 100%;">';
let clusterMode = data[0]['clusterMode'];
if (clusterMode === 'true') {
    html += '<tb-cluster-info-table></tb-cluster-info-table>';
} else {
    html += '<tb-dashboard-state stateId="monolith_system_info" [ctx]="ctx"></tb-dashboard-state>';
}
html += '</div>';
return html;

dataKeys.postFuncBody:
var status = 'status-ok';
if (value > 85) {
    status = 'status-critical';
} else if (value > 75) {
    status = 'status-warn';
}
return status;

var text = '';
if (value > 85) {
    text = '{{ \'widgets.system-info.cpu-critical-text\' | translate }}';
} else if (value > 75) {
    text = '{{ \'widgets.system-info.cpu-warning-text\' | translate }}';
}
return text;

