    line-height: 1;
}

.tb-status.status-ok:after {
    content: "check";
    color: #198038;
    font-weight: 600;
}

.tb-status.status-warn:after {
    content: "warning";
    color: #FAA405;
}

.tb-status.status-critical:after {
    content: "warning";
    color: #D12730;
}

.tb-value-container {
    user-select: none;
}

.tb-count {
    font-style: normal;
    font-weight: 500;
    font-size: 26px;
    line-height: 36px;
}

.tb-total {
    padding-left: 4px;
    font-weight: 500;
    font-size: 14px;
    line-height: 20px;
    color: rgba(0, 0, 0, 0.54);
}

