}

settings.dashboardCss:
.tb-widget-container > .tb-widget {
    border: 1px solid rgba(0, 0, 0, 0.05);
    box-shadow: 0px 5px 16px rgba(0, 0, 0, 0.04);
    border-radius: 12px;
}

.tb-widget-container > .tb-widget:not([style*="padding: 0"]) {
    padding: 16px !important;
}

.tb-card-title {
    display: grid;
}

.tb-home-widget-title {
    font-style: normal;
    font-weight: 500;
    font-size: 14px;
    line-height: 20px;
    letter-spacing: 0.25px;
    color: rgba(0, 0, 0, 0.54);
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.tb-home-widget-link {
    position: relative;
    border-bottom: none;
}

.tb-home-widget-link:hover {
    border-bottom: none;
}

.tb-home-widget-link:focus {
    border-bottom: none;
}

.tb-home-widget-link::after {
    content: 'arrow_forward';
    display: inline-block;
    transform: rotate(315deg);
    font-family: 'Material Icons';
    font-weight: normal;
    font-style: normal;
    font-size: 18px;
    color: rgba(0, 0, 0, 0.12);
    vertical-align: bottom;
    margin-left: 6px;    
}

.tb-home-widget-link:hover::after {
    color: inherit;
}

.tb-home-widget-info-icon {
    color: rgba(0, 0, 0, 0.12);
    font-size: 16px;
    width: 16px;
    height: 16px;
    line-height: 15px;
    vertical-align: middle;
}

.tb-widget-container > .tb-widget .tb-timewindow {
    font-size: 14px;
    line-height: 20px;
    letter-spacing: 0.25px;
    color: rgba(0, 0, 0, 0.54);
    padding: 0;
}

.tb-widget-container > .tb-widget .tb-legend-keys .tb-legend-label {
    cursor: pointer;
    user-select: none;
    font-weight: 400;
    font-size: 14px;
    line-height: 20px;
    letter-spacing: 0.2px;
    color: rgba(0, 0, 0, 0.54);
}

@media screen and (min-width: 960px) and (max-width: 1279px) {
    .tb-widget-container > .tb-widget {
        border-radius: 4px;
    }
    .tb-widget-container > .tb-widget:not([style*="padding: 0"]) {
        padding: 2px !important;
    }
    .tb-hide-md {
        display: none;
    }
}

@media screen and (min-width: 1280px) and (max-width: 1819px) {
    .tb-widget-container > .tb-widget:not([style*="padding: 0"]) {
        padding: 8px !important;
    }
    .tb-hide-lg {
        display: none;
    }
}

@media screen and (min-width: 960px) and (max-width: 1819px) {
    .tb-hide-md-lg {
        display: none;
    }

    .tb-home-widget-title {
        font-size: 12px;
        line-height: 16px;
    }

    .tb-widget-container > .tb-widget .tb-widget-title {
        padding: 0;
    }

    .tb-widget-container > .tb-widget .tb-timewindow {
        font-size: 12px;
        line-height: 16px;
        min-height: 24px;
        padding: 0;
    }

