        width: 18px;
        height: 18px;
        font-size: 18px;
    }

    .tb-widget-container > .tb-widget tb-legend {
        padding-bottom: 0 !important;
    }

    .tb-widget-container > .tb-widget .tb-legend-keys .tb-legend-label {
        font-size: 11px;
        line-height: 16px;
        letter-spacing: 0.25px;
    }
}

